module.exports = {
  extends: './eslintrc.react.js',
  plugins: ['react', 'react-native'],
  env: {
    browser: true,
    es6: true,
    'react-native/react-native': true,
  },
  rules: {
    // mrn中 react native相关的库都不在package.json中
    'import/no-extraneous-dependencies': 'off',
    // 内联样式符合集中原则，但会导致样式的分散维护，故设置为 warn，不作强制，开发者自行决定是否处理
    'react-native/no-inline-styles': 'warn',
    // 颜色字面量可以避免样式的分散维护，但对于只用一次的色值，强行变量化有悖于集中原则，故设置为 off，不作强制，开发者自行决定是否处理
    'react-native/no-color-literals': 'off',
    // style的属性声明按字母排序, 将一个模块相关的样式放在一起方便查看
    'react-native/sort-styles': 'off',
  },
}
